targets.expr:
sum by (message_type) (
  count_over_time(
    {couchbase_cluster_name="$cluster",file="memcached.log",couchbase_node=~"$node",level="WARN"} 
    | json  
    | label_format message_type=`{{ regexReplaceAll "^(\\d+\\s+)|\\([^)]+\\)\\s*|[0-9]+:\\s*|(\\.\\s+)?(\\{).+$|on vb:[0-9]+|on thread.+$|\\s+for\\s.+$|,.+|\\s+\\[.+$" .message "" }}` 
    | __error__="" [$interval]
  )
)

sum by (message_type) (
  count_over_time(
    {couchbase_cluster_name="$cluster",file="memcached.log",couchbase_node=~"$node"} 
    |= "Slow runtime" 
    | json      
    | label_format message_type=`{{ regexReplaceAll "^.+'([^.']+)\\.?'.+" .message "${1}" }}`    
    | label_format message_type=`{{ regexReplaceAll "on\\s+vb:.+" .message_type "" }}` 
    | __error__="" [$interval]
  )
)

