targets.expr:
topk(5, 
  sum(
    rate(
      namedprocess_namegroup_cpu_seconds_total{cluster_name=~"$cluster",instance=~"$node",groupname=~"$processes",mode=~"user|system"}[$__rate_interval]
    ) 
  ) by (instance, groupname)
  or 
  sum(
    irate(
      namedprocess_namegroup_cpu_seconds_total{cluster_name=~"$cluster",instance=~"$node",groupname=~"$processes",mode=~"user|system"}[$__rate_interval]
    )
  ) by (instance, groupname)
)
