targets.expr:
sum (
  count_over_time(
    {couchbase_cluster_name="$cluster", file="slow_query.log", level="INFO", bucket=~"$bucket"} [$__range]
  )
)

sum by(users) (
  count_over_time(
    {couchbase_cluster_name="$cluster", file="slow_query.log", couchbase_node=~"$node", bucket=~"$bucket"} 
    | json
    | __error__="" [$__range]
  )
)

sum by(remoteAddr) (
  count_over_time(
    {couchbase_cluster_name="$cluster", file="slow_query.log", bucket=~"$bucket"} 
    | json 
    | label_format remoteAddr=`{{ regexReplaceAll ":[0-9]+" .remoteAddr "" }}`
    | __error__="" [$__range]
  )
)

sum by(userAgent) (
  count_over_time(
    {couchbase_cluster_name="$cluster", file="slow_query.log", bucket=~"$bucket", couchbase_node=~"$node"} 
    | json 
    | label_format userAgent=`{{ regexReplaceAll "\\s.+" .userAgent "" }}`
    | __error__="" [$__range]
  )
)

avg (
  avg_over_time(
    {couchbase_cluster_name="$cluster", file="slow_query.log", level="INFO", bucket=~"$bucket", couchbase_node=~"$node"}
    | json 
    | unwrap querySelectivityPercent 
    | errorCount == 0 [$__range]
  )
)

avg (
  avg_over_time(
    {couchbase_cluster_name="$cluster", file="slow_query.log", level="INFO", bucket=~"$bucket", couchbase_node=~"$node"}
    | json 
    | unwrap resultSize 
    | __error__="" [$interval]
  )
)

avg (
  avg_over_time(
    {couchbase_cluster_name="$cluster", file="slow_query.log", level="INFO", bucket=~"$bucket", couchbase_node=~"$node"}
    | json 
    | unwrap resultCount 
    | __error__="" [$interval]
  )
)

avg (
  avg_over_time(
    {couchbase_cluster_name="$cluster", file="slow_query.log", level="INFO", bucket=~"$bucket", couchbase_node=~"$node"}
    | json 
    | unwrap phaseCounts_indexScan 
    | __error__="" [$interval]
  )
