avg (
  avg_over_time(
    {couchbase_cluster_name="$cluster", file="slow_query.log", level="INFO", bucket=~"$bucket", couchbase_node=~"$node"}
    | json 
    | unwrap elapsedTimeMs 
    | errorCount == 0 [$interval]
  )
)

avg (
  avg_over_time(
    {couchbase_cluster_name="$cluster", file="slow_query.log", level="INFO", bucket=~"$bucket", couchbase_node=~"$node"}
    | json 
    | unwrap phaseCounts_fetch 
    | errorCount == 0 [$interval]
  )
)

sum by(bucket) (
  count_over_time(
    {couchbase_cluster_name="$cluster", file="slow_query.log", bucket=~"$bucket"} [$__range]
  )
)

sum by (bucket) (
  count_over_time(
    {couchbase_cluster_name="$cluster", file="slow_query.log", level="INFO", bucket=~"$bucket"} 
    | json 
    | errorCount > 0 [$__range]
  )
)

sum by (bucket) (
  count_over_time(
    {couchbase_cluster_name="$cluster", file="slow_query.log", level="INFO", bucket=~"$bucket"} [$interval]
  )
)

sum by (bucket) (
  sum_over_time(
    {couchbase_cluster_name="$cluster", file="slow_query.log", level="INFO", bucket=~"$bucket"}
    | json 
    | unwrap phaseCounts_fetch 
    | __error__="" [$interval]
  )
)

sum by (bucket) (
  sum_over_time(
    {couchbase_cluster_name="$cluster", file="slow_query.log", level="INFO", bucket=~"$bucket"}
    | json 
    | unwrap resultSize 
    | __error__="" [$interval]
  )
)

sum by (bucket) (
  avg_over_time(
    {couchbase_cluster_name="$cluster", file="slow_query.log", level="INFO", bucket=~"$bucket"}
    | json 
    | unwrap querySelectivityPercent 
    | __error__="" [$interval]
  )
)

sum by (bucket) (
  avg_over_time(
    {couchbase_cluster_name="$cluster", file="slow_query.log", level="INFO", bucket=~"$bucket"}
    | json 
    | unwrap elapsedTimeMs 
    | __error__="" [$interval]
  )
)

topk(
  $top,
  sum by (bucket, scope, collection, users, statement) (
    count_over_time(
      {couchbase_cluster_name="$cluster", file="slow_query.log", level="INFO", bucket=~"$bucket"} 
      | json
      [$__range]
    )
  )
)

topk(
  $top, 
  max_over_time(
    {couchbase_cluster_name="$cluster", file="slow_query.log", level="INFO", bucket=~"$bucket"} 
    | json 
    | unwrap elapsedTimeMs
    | __error__=""[$__range]
  )
  by (bucket, scope, collection, users, statement)
)

topk(
  $top,
  sum by (bucket, scope, collection, users, statement) (
    count_over_time(
      {couchbase_cluster_name="$cluster", file="slow_query.log", level="INFO", bucket=~"$bucket"} 
      | json 
      | phaseCounts_primaryScan > 0
      [$__range]
    )
  )
)

topk(
  $top, 
  avg_over_time(
    {couchbase_cluster_name="$cluster", file="slow_query.log", level="INFO", bucket=~"$bucket"} 
    | json 
    | unwrap cost
    | __error__=""[$__range]
  )
  by (bucket, scope, collection, users, statement)
)

bottomk(
  $top, 
  max_over_time(
    {couchbase_cluster_name="$cluster", file="slow_query.log", level="INFO", bucket=~"$bucket"} 
    | json 
    | unwrap querySelectivityPercent
    | __error__=""[$__range]
  )
  by (bucket, scope, collection, users, statement)
)
